DROP TABLE IF EXISTS `#__attendance_type`;DROP TABLE IF EXISTS `#__attendance_items`;CREATE TABLE `#__attendance_type` (    `id`            INT NOT NULL AUTO_INCREMENT,    `shortname`     TEXT NOT NULL,    `longname`      TEXT,    `sortkey`       INT,    `published`     INT NOT NULL,    `created`       TIME,    `creator`       TEXT,    `modified`      TIME,    `modifier`      TEXT,    PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT COLLATE=utf8_general_ci;CREATE TABLE `#__attendance_items` (    `id` 	    INT  NOT NULL AUTO_INCREMENT,    `year`  	    INT,    `number` 	    INT,    `name`  	    TEXT(50) NOT NULL,    `date`  	    DATE,    `type` 	    INT,    `mode`	    TEXT,    `place`	    INT,    `teams`	    INT,    `driver`	    TEXT,    `team`          TEXT,    `distance`	    REAL(5,2),    `fee`	    REAL(5,2),    `fare`	    REAL(5,2),    `currency`	    TEXT,      `comment`	    TEXT,    `sortkey`	    INT,	    `published`	    TINYINT(1) NOT NULL DEFAULT 1,    `publishedfrom` TIME,    `publishedto`   TIME,    `created`	    TIME,    `creator`       TEXT,    `modified`	    TIME,    `modifier`      TEXT,  PRIMARY KEY (`id`),  KEY `attendance_items` (`id`)) ENGINE=MyISAM DEFAULT COLLATE=utf8_general_ci;INSERT INTO `#__attendance_type` (`shortname`, `longname`, `sortkey`, `published`, `created`, `creator`)                          VALUES ('Herren', 'Herren', 1, 1, now(), 'install'); INSERT INTO `#__attendance_type` (`shortname`, `longname`, `sortkey`, `published`, `created`, `creator`)                          VALUES ('Damen', 'Damen', 2, 1, now(), 'install'); INSERT INTO `#__attendance_type` (`shortname`, `longname`, `sortkey`, `published`, `created`, `creator`)                          VALUES ('Mix', 'Mix', 3, 1, now(), 'install'); 													 INSERT INTO `#__attendance_type` (`shortname`, `longname`, `sortkey`, `published`, `created`, `creator`)                          VALUES ('Duo-Herren', 'Duo-Herren', 4, 1, now(),